function getFizzBuzz() {
 const Fizz BuzzArray = [];
 for (let i = 1; i <= 100; i++) {
  if (i % 3 === 0 && i % 5 === 0) {
   Fizz BuzzArray.push("Fizz Buzz");
  } else if (i % 3 === 0) {
   Fizz BuzzArray.push("Fizz");
  } else if (i % 5 === 0) {
   Fizz BuzzArray.push("Buzz");
